CREATE SEQUENCE admin_idadmin_seq START WITH 1 INCREMENT BY 1;

CREATE SEQUENCE article_id_seq START WITH 1 INCREMENT BY 1;

CREATE  TABLE "admin" ( 
	idadmin              integer DEFAULT nextval('admin_idadmin_seq'::regclass) NOT NULL  ,
	username             varchar(100)    ,
	"password"           varchar(150)    ,
	CONSTRAINT pk_tbl PRIMARY KEY ( idadmin )
 );

CREATE  TABLE article ( 
	id                   integer DEFAULT nextval('article_id_seq'::regclass) NOT NULL  ,
	titre                text    ,
	contenu              text    ,
	resume               text    ,
	visual               text    ,
	CONSTRAINT pk_article PRIMARY KEY ( id )
 );

